import React from 'react';
import './Card.css';
export default propriedades => {

   const etilo = {
        backgroundColor: propriedades.color|| '#F00',
        borderColor: propriedades.color || '#F00'
    }

    return (
        <div className='Card'>
            <div className='Title' style={etilo}>{propriedades.titulo}</div>
            <div className='Content'>
                {propriedades.children}
            </div>
        </div>

    )
}
