/**
 * 
 *  <Inf test={exp}>
 *  <span>...</span>
 *  <span>...</span>
 *  <span>...</span>
 * 
 * </Inf>
 * 
 */

export default props => {

    if (props.test) {
        return props.children
    } else {
        return false
    }

}
