import React, { useState } from "react";

export default props => {

    function gerarNumeroNaoContido(min, max, array) {

        const random = parseInt(Math.random() * (max + 1 - min)) + min;

        return array.includes(random) ? gerarNumeroNaoContido(min, max, array) : random

    }

    function gerarNumero(params) {

        const numeros = Array(params)
            .fill(0)
            .reduce((acc) => {
                const newNumber = gerarNumeroNaoContido(1, 60, acc)
                return [...acc, newNumber]
            }, [])
            .sort((n1, n2) => n1 - n2)

        return numeros

    }
    const numerosInicias = Array(props.params || 6).fill(0)
    const [numeros, setNumbers] = useState(Array(props.params || 6).fill(0))

    return (
        <div>

            <h2>MEGA</h2><h3>{numeros.join(' ')}</h3>
            <button onClick={_ => setNumbers(gerarNumero)}>Gerar Numeros</button>
        </div>
    );
}
