import React, { Component } from "react";
import './contador.css'
class Contador extends Component {


    state = {
        numero: this.props.numeroInicil || 0,
        passo: this.props.passoInicil || 5,
    }

    inc = () => {
        this.setState({
            numero: this.state.numero + this.state.passo,
        });
    }

    dec = () => {
        this.setState({
            numero: this.state.numero - this.state.passo,
        });
    }

    setPasso = (e) => {
        this.setState({
            passo: +e.target.value
        })
    }
    render() {
        return (<>
            <div className="Contador">
                <h2>Contador</h2>
                <div id="Contador">
                    <h3>{this.state.numero}</h3>
                    <label htmlFor="passoInput">Passo: </label>
                    <input id="passoInput" type="number" value={this.state.passo} onChange={this.setPasso} />
                    <div className="Buttons">
                    <button id='green' onClick={this.inc}>+</button>
                    <button id='red' onClick={this.dec}>-</button>
                    </div>
                </div>
           </div >
            <div className="Contador">
            <h2>Contador</h2>
                <h3>{this.state.numero}</h3>
                <label htmlFor="passoInput">Passo: </label>
                <input id="passoInput" type="number" value={this.state.passo} onChange={this.setPasso} />
                <button  onClick={this.inc}>+</button>
                <button  onClick={this.dec}>-</button>
            </div> 
            </>
        );
    }

}

export default Contador
